export const GET_MOVIES = 'GET_MOVIES'
export const GET_MOVIE_DETAIL = 'GET_MOVIE_DETAIL'
export const CLEAR_MOVIES_DETAIL = 'CLEAR_MOVIES_DETAIL'
export const ADD_MOVIE_FAVORITE = 'ADD_MOVIE_FAVORITE'
export const REMOVE_MOVIE_FAVORITE = 'REMOVE_MOVIE_FAVORITE'
  
  export function getMovies(titulo) {
    return async function(dispatch) {
      const response = await fetch("http://www.omdbapi.com/?apikey=f42a4829" + titulo)
      const json = await response.json()
      console.log(json)
      dispatch({ type: GET_MOVIES, payload: json })
    };
  }


  export function getMovieDetail(id) {
    if(id)
      return async function(dispatch) {
        const response = await fetch("http://www.omdbapi.com/?apikey=f42a4829" + id)
        const json = await response.json()
        dispatch({ type: GET_MOVIE_DETAIL, payload: json })
      };
    return {type: CLEAR_MOVIES_DETAIL }
  }


export function addMovieFavorite(payload) {
    return { 
        type: ADD_MOVIE_FAVORITE, 
        payload
    };
  }


export function removeMovieFavorite(payload) {
    return { 
        type: REMOVE_MOVIE_FAVORITE, 
        payload
    };
  }
